	(236,'external_updates-espresso-permissions-basic','O:8:\"stdClass\":3:{s:9:\"lastCheck\";i:1359415518;s:14:\"checkedVersion\";s:5:\"1.5.3\";s:6:\"update\";O:19:\"PluginUpdateUtility\":7:{s:2:\"id\";s:4:\"5741\";s:4:\"slug\";s:26:\"espresso-permissions-basic\";s:7:\"version\";s:5:\"1.5.3\";s:8:\"homepage\";s:36:\"/product/espresso-permissions-basic/\";s:12:\"download_url\";s:237:\"http://eventespresso.com?pu_request_plugin=espresso-permissions-basic&pu_get_download=1&pue_active_version=1.5.3&site_domain=localhost%3A8888%2FPLS&pue_install_key=2123215882&pu_plugin_api=9ea20a5279a779c3a195921b61912d76&new_pue_check=1\";s:8:\"sections\";O:8:\"stdClass\":3:{s:11:\"description\";s:0:\"\";s:12:\"installation\";s:0:\"\";s:9:\"changelog\";s:171:\"<ul>\r\n	<li><em>Fixes</em>\r\n<ul>\r\n	<li>When saving an event, selected question groups for additional attendees do not save when event is published</li>\r\n</ul>\r\n</li>\r\n</ul>\";}s:14:\"upgrade_notice\";s:187:\"This is a HOTFIX release for Roles & Permissions.  If you are using the BASIC version of Espresso Permissions, it is <strong>strongly recommended</strong> that you update to this version.\";}}','yes'),
